import React from 'react';

class App extends React.component{
  state = {
    isLoading: true
  };
  componentDidMount(){
    setTimeout(() => {
      this.setState({isLoading: false}, 4000)
    })
  }
  render(){
    const { isLoading } = this.state; //es6?
    return <div>{isLoading ? "Loading" : "We are ready"} </div>;
  }
}

export default App;
